document.querySelectorAll(".item__link").forEach(e=>{e.addEventListener("click",function(){setTimeout(function(){document.querySelectorAll(".tag").forEach(e=>{e.style.display="none"})},787)})}),document.querySelectorAll(".unbutton").forEach(e=>{e.addEventListener("click",function(){setTimeout(function(){document.querySelectorAll(".tag").forEach(e=>{e.style.display="block"})},650)})});